import React from 'react';
import { Link } from 'react-router-dom';

function importAll(r) {
    return r.keys().map(r);
}

const productImages = importAll(require.context('../../front/img/products/', false, /\.(png|jpe?g|svg)$/));

function Detail_produit() {
    return (

    <div className="row m-sm-0">
        <div className="col-sm-2 p-sm-0 order-2 order-sm-1 mt-2 mt-sm-0 px-xl-2">
          <div className="swiper product-slider-thumbs">
            <div className="swiper-wrapper">
              <div className="swiper-slide h-auto swiper-thumb-item mb-3"><img className="w-100" src="img/product-detail-1.jpg" alt="..." /></div>
              <div className="swiper-slide h-auto swiper-thumb-item mb-3"><img className="w-100" src="img/product-detail-2.jpg" alt="..." /></div>
              <div className="swiper-slide h-auto swiper-thumb-item mb-3"><img className="w-100" src="img/product-detail-3.jpg" alt="..." /></div>
              <div className="swiper-slide h-auto swiper-thumb-item mb-3"><img className="w-100" src="img/product-detail-4.jpg" alt="..." /></div>
            </div>
          </div>
        </div>
        <div className="col-sm-10 order-1 order-sm-2">
          <div className="swiper product-slider">
            <div className="swiper-wrapper">
              <div className="swiper-slide h-auto"><Link className="glightbox product-view" href="img/product-detail-1.jpg" data-gallery="gallery2" data-glightbox="Product item 1">
              <img className="img-fluid" src="img/product-detail-1.jpg" alt="..." /></Link></div>
              <div className="swiper-slide h-auto"><Link className="glightbox product-view" href="img/product-detail-2.jpg" data-gallery="gallery2" data-glightbox="Product item 2">
                <img className="img-fluid" src="img/product-detail-2.jpg" alt="..."/></Link></div>
              <div className="swiper-slide h-auto"><Link className="glightbox product-view" href="img/product-detail-3.jpg" data-gallery="gallery2" data-glightbox="Product item 3">
                <img className="img-fluid" src="img/product-detail-3.jpg" alt="..."/></Link></div>
              <div className="swiper-slide h-auto"><Link className="glightbox product-view" href="img/product-detail-4.jpg" data-gallery="gallery2" data-glightbox="Product item 4">
                <img className="img-fluid" src="img/product-detail-4.jpg" alt="..."/></Link></div>
            </div>
          </div>
        </div>
      </div>
    
        );

        

    }

export default Detail_produit;
