import React from 'react';
import { Link } from 'react-router-dom';

const descript = [
    {
      name: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. In ut ullamcorper leo, eget euismod orci. Cum sociis natoque',
      titre: 'Red digital smartwatch',
    },
  ];

function Description() {
    return (
        <div className="tab-content mb-5" id="myTabContent">
        <div className="tab-pane fade show active" id="description" role="tabpanel" aria-labelledby="description-tab">
        {descript.map((product, index) => (
          <div className="p-4 p-lg-5 bg-white">
            <h6 className="text-uppercase">{product.titre} </h6>
            <p className="text-muted text-sm mb-0">{product.name} </p>
          </div>
           ))}
        </div>
        </div>
    );
}

export default Description;
